var fixer = require('../lib/index.js');

function getRandomInt(min, max) {
  min = Math.ceil(min);
  max = Math.floor(max);
  return Math.floor(Math.random() * (max - min)) + min;
}

fixer.get(function (err, res, body) {
    console.log(err?err:body);
    console.log(fixer.allowedRates());
    for (var i = 0; i < 10; i++) {
        var rates = fixer.allowedRates();
            fromCurrency = rates[getRandomInt(0, rates.length)],
            toCurrency = rates[getRandomInt(0, rates.length)],
            oldAmount = getRandomInt(0, 100),
            newAmount = fixer.convert(fromCurrency, toCurrency, oldAmount);
        console.log(fromCurrency, "->", toCurrency, ":", oldAmount, "->", newAmount);
    }
});